<?phpnamespace App\Controller\Admin;use App\Repository\CategoryRepository;use Doctrine\ORM\EntityManagerInterface;use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;use Symfony\Component\Form\Extension\Core\Type\SubmitType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Routing\Annotation\Route;class GestionCategoryController extends AbstractController{    /**     * @Route("gestion/category", name="gestion_category")     */    public function GestionCategory(EntityManagerInterface $manager, CategoryRepository $categoryRepository)    {        $categoryFromDb = $categoryRepository->findAll();        return $this->render('gestion/GestionCategory.html.twig',            ['categories' => $categoryFromDb]        );    }    /**     * @Route("change/category")     */    public function change()    {        $form = $this->createFormBuilder()            ->add('title', TextType::class)            ->add('save', SubmitType::class, [                'label' => 'Enregistrer'            ])            ->getForm()       ##return $this->render('gestion/ModificationForm.html.twig',          ## ['form' => $form=>createView()]        )    }}