<?phpnamespace App\Controller;use App\Entity\Animal;use App\Repository\AnimalRepository;use App\Repository\CategoryRepository;use App\Repository\RaceRepository;use App\Repository\SpecieRepository;use Doctrine\ORM\EntityManagerInterface;use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;use Symfony\Component\Form\Extension\Core\Type\ChoiceType;use Symfony\Component\Form\Extension\Core\Type\NumberType;use Symfony\Component\Form\Extension\Core\Type\SubmitType;use Symfony\Component\HttpFoundation\Request;use Symfony\Component\Routing\Annotation\Route;/** * Class AnimalProfileController */class AnimalProfileController extends AbstractController{    /**     * @Route("/profile/{id}", name="profile_show")     */    public function showProfile($id ,EntityManagerInterface $manager, AnimalRepository $animalRepository)    {        $repository = $manager->getRepository(Animal::class);        $animal = $repository->find($id);        return $this->render('animal/Profile.html.twig',            [                'animal' => $animal            ]        );    }    /**     * @Route("/search", name="search_profile")     */    public function searchProfile(Request $request, CategoryRepository $categoryRepository, SpecieRepository $specieRepository, AnimalRepository $animalRepository)    {        $speciesFromDb = $specieRepository->findAll();        $species = [];        foreach ($speciesFromDb as $item) {            $species[$item->getName()] = $item->getId();        }        $categoryFromDb = $categoryRepository->findAll();        $category = [];        foreach ($categoryFromDb as $cate) {            $category[$cate->getTitle()] = $cate->getId();        }        $form = $this->createFormBuilder()            ->add('types',                ChoiceType::class,                [                    'placeholder' => 'Choisissez un type',                    'choices' => $species                ]            )            ->add('race',                ChoiceType::class,                [                    'disabled' => true,                    'choices' => [],                ]            )            ->add('sexe',                ChoiceType::class,                [                    'empty_data' => null,                    'choices' => [                        'Tous' => null,                        'Mâle' => 'm',                        'Femelle' => 'f',                    ],                ])            ->add('age', NumberType::class, [                    'html5' => true,                    'attr' => [                        'min' => 1,                        'max' => 10                    ],                ]            )            ->add('categories',                ChoiceType::class,                [                    'placeholder' => 'Choisissez une catégorie',                    'choices' => $category                ])            ->add('save', SubmitType::class, [                'label' => 'Enregistrer'            ])            ->getForm();        $qb = $animalRepository->createQueryBuilder("animal");        $form->handleRequest($request);        if ($form->isSubmitted() && $form->isValid()) {            $data = $form->getData();            if(isset($data["sexe"]) && !empty($data["sexe"])){                $qb->andWhere("animal.gender = :gender")->setParameter("gender", $data["sexe"]);            }            if(isset($data["age"]) && !empty($data["age"])){                $qb->andWhere("animal.age = :age")->setParameter("age", $data["age"]);            }        }        $animals = $qb->getQuery()->execute();        return $this->render('animal/SearchProfile.html.twig',            [                "animals" => $animals,                'ProfileSearch' => $form->createView()            ]        );    }    /**     * @Route("/ajax/species/{id}/races", name="ajax_get_races_select")     */    public function ajaxGetRacesSelect(int $id, RaceRepository $raceRepository)    {        $races = $raceRepository->findBy([            "species" => ["id" => $id]        ]);        return $this->render('animal/SelectRace.html.twig',            [                "id" => $id,                "races" => $races            ]        );    }}